mapper.select:
SELECT NVL(MAX(ROWNUM), 0)
FROM toon_comment
WHERE code=#{code}

mapper.update:
update toon_comment 
set likeCount=likeCount+1 
where commcode= #{commcode}

update toon_comment 
set likeCount=likeCount-1
where commcode= #{commcode}

mapper.select:
SELECT commcode
FROM  commentlike  
WHERE id=#{id} and code=#{code}

mapper.update:
UPDATE toon_comment
set content=#{content}
where num=#{num}
